import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Spinner from './Spinner';
export class News extends Component {
    
    constructor() {
        super(); //Always used when we create constructor
        console.log("Hello I am a constructir from news component");
        this.state = {
            articles: [],
            loading: false,
            page:1
        }
    }

async componentDidMount(){ //Sbse phle constructor aaega phir render phir cdm , async func waits for some promises to fullfill
            let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=d6753bb2566b4e95bef87f64a633187e&page=1&pageSize=${this.props.pageSize}`;
            let data = await fetch(url); //fetch api takes url and returns a promise
            let parsedata= await data.json()
            console.log(parsedata);
            this.setState({articles: parsedata.articles, totalResults: parsedata.totalResults}) // articl ko set kr rhe parsed.article k brabr
        }

    handlePreviousClick= async ()=>{
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=d6753bb2566b4e95bef87f64a633187e&page=${this.state.page -1}&pageSize=${this.props.pageSize}`;
        let data = await fetch(url); //fetch api takes url and returns a promise
        let parsedata= await data.json()
        console.log(parsedata);
        this.setState({
            articles: parsedata.articles,
            page: this.state.page -1,

        })

    }
    handleNextClick= async ()=>{
        if (this.state.page +1 > Math.ceil(this.state.totalResults/this.props.pageSize)) {
            //pageSize=18 taken in App.js
        }
        else{
            let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=d6753bb2566b4e95bef87f64a633187e&page=${this.state.page +1}&pageSize=${this.props.pageSize}`;
            let data = await fetch(url); //fetch api takes url and returns a promise
            let parsedata= await data.json()
            console.log(parsedata);
            this.setState({
                articles: parsedata.articles,
                page: this.state.page +1,

            })
        }
    }
    render() {
        return (
            <div className="container my-3">
                <h1 className="text-center">Mai News Hun!</h1>
                {<Spinner/>}
                <div className="row">
                    {this.state.articles.map((element) => {
                        return <div className="col-md-4" key={element.url}>
                            <NewsItem title={element.title?element.title.slice(0, 45):""} description={element.description?element.description.slice(0, 88):""} imageUrl={element.urlToImage} newsUrl={element.url} />
                        </div>
                    })}
                </div>
                <div className="container d-flex justify-content-between">
                <button disabled={this.state.page<=1} type="button" className="btn btn-dark" onClick={this.handlePreviousClick}>&larr; Previous</button>
                <button disabled={this.state.page +1 > Math.ceil(this.state.totalResults/this.props.pageSize)}type="button" className="btn btn-dark" onClick={this.handleNextClick}>Next &rarr;</button>
                </div>
            </div>
        )
    }
}

export default News
