import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class News extends Component {
    
    constructor() {
        super(); //Always used when we create constructor
        console.log("Hello I am a constructir from news component");
        this.state = {
            articles: [],
            loading: false,
            page:1
        }
    }

async componentDidMount(){ //Sbse phle constructor aaega phir render phir cdm , async func waits for some promises to fullfill
            let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=d6753bb2566b4e95bef87f64a633187e";
            let data = await fetch(url); //fetch api takes url and returns a promise
            let parsedata= await data.json()
            console.log(parsedata);
            this.setState({articles: parsedata.articles}) // articl ko set kr rhe parsed.article k brabr
        }

    render() {
        return (
            <div className="container my-3">
                <h1>Mai News Hun!</h1>
                <div className="row">
                    {this.state.articles.map((element) => {
                        return <div className="col-md-4" key={element.url}>
                            <NewsItem title={element.title?element.title.slice(0, 45):""} description={element.description?element.description.slice(0, 88):""} imageUrl={element.urlToImage} newsUrl={element.url} />
                        </div>
                    })}
                </div>
                <div className="container" >
                <button type="button" class="btn btn-dark">Previous</button>
                <button type="button" class="btn btn-dark">Next</button>
                </div>
            </div>
        )
    }
}

export default News
